import { act } from 'react-dom/test-utils';

jest.mock('next/navigation', () => ({
  useRouter: () => ({
    push: jest.fn(),
    replace: jest.fn(),
    refresh: jest.fn(),
  }),
}));

const mockDocumentData = {
  id: 'doc-1',
  content: 'テスト文書内容',
  status: 'pending'
};

const mockAIResults = {
  documentId: 'doc-1',
  analysis: {
    issues: [
      {
        type: 'warning',
        message: '要確認項目があります',
        location: { start: 0, end: 10 }
      }
    ],
    score: 85,
    suggestions: ['修正提案1', '修正提案2']
  }
};

